elgg.provide("framework.tabs.sortable");framework.tabs.sortable.init=function(){$("#hj-sortable-tabs > ul").sortable({items:"li:has(> .elgg-state-draggable)",axis:"x",cursor:"move",forcePlaceholderSize:true,placeholder:"elgg-widget-placeholder",opacity:.8,revert:100,stop:framework.tabs.sortable.moveTab})};framework.tabs.sortable.moveTab=function(a,b){var c=$("#hj-sortable-tabs > ul").sortable("toArray");var d=$.map(c,function(a){return a.replace("hj-ajax-tab-load-","")});elgg.system_message(elgg.echo("framework:processing"));elgg.action("action/framework/entities/move",{data:{priorities:d}})};elgg.register_hook_handler("init","system",framework.tabs.sortable.init);elgg.register_hook_handler("success","ajax",framework.tabs.sortable.init);